import { MimeType } from '../../../constants';
import { DocumentIcon, MediaIcon, VideoIcon } from '../../../icons';
import getIconFromType from '../../src/utils/get-icon-from-type';
var imageMimeTypes = Object.values(MimeType).filter(function (mimeType) {
  return mimeType.includes('image');
});
var videoMimeTypes = Object.values(MimeType).filter(function (mimeType) {
  return mimeType.includes('video');
});
var documentMimeTypes = Object.values(MimeType).filter(function (mimeType) {
  return !imageMimeTypes.includes(mimeType) && !videoMimeTypes.includes(mimeType);
});
describe('getIconFromType', function () {
  it.each(imageMimeTypes)('should return MediaIcon when mimeType is %p', function (mimeType) {
    var result = getIconFromType(mimeType);
    expect(result).toBe(MediaIcon);
  });
  it.each(videoMimeTypes)('should return VideoIcon when mimeType is %p', function (mimeType) {
    var result = getIconFromType(mimeType);
    expect(result).toBe(VideoIcon);
  });
  it.each(documentMimeTypes)('should return DocumentIcon when mimeType is %p', function (mimeType) {
    var result = getIconFromType(mimeType);
    expect(result).toBe(DocumentIcon);
  });
});