import { MimeType } from '../../../constants';
import mimeTypeToExtension from '../../src/utils/mime-type-to-extension';
describe('mimeTypeToExtension', function () {
  it.each(Object.entries(MimeType))('should return .%s when mimeType is %p', function (key, mimeType) {
    var result = mimeTypeToExtension(mimeType);
    expect(result).toBe(".".concat(key));
  });
  it('should return undefined when mimeType cannot be found', function () {
    var badMimeType = 'does-not/exist';
    var result = mimeTypeToExtension(badMimeType);
    expect(result).toBeUndefined();
  });
});