import { MimeType } from '../../../constants';
import mimeTypesToExtensions from '../../src/utils/mime-types-to-extensions';
describe('mimeTypesToExtensions', function () {
  it('should return extensions based on mimeTypes', function () {
    var mimeTypes = Object.values(MimeType);
    var expected = Object.keys(MimeType).map(function (key) {
      return ".".concat(key);
    });
    var result = mimeTypesToExtensions(mimeTypes);
    expect(result).toStrictEqual(expected);
  });
  it('should discard mimeTypes not found', function () {
    var badMimeType = 'does-not/exist';
    var expected = [];
    var result = mimeTypesToExtensions([badMimeType]);
    expect(result).toStrictEqual(expected);
  });
});