import { buildFile } from '../../../test/utils';
import getAcceptedFiles from '../../src/utils/get-accepted-files';
import getFileRejections from '../../src/utils/get-file-rejections';
import splitFiles from '../../src/utils/split-files';
describe('splitFiles', function () {
  it('returns expected accepted/rejected values', function () {
    var maxSizeInBytes = 10000;
    var fileUnderLimit = buildFile({
      size: maxSizeInBytes - 1
    });
    var fileOverLimit = buildFile({
      size: maxSizeInBytes + 1
    });
    var files = [fileUnderLimit, fileOverLimit];
    var result = splitFiles(files, {
      maxSizeInBytes: maxSizeInBytes
    });
    expect(result.accepted).toStrictEqual(getAcceptedFiles(files, {
      maxSizeInBytes: maxSizeInBytes
    }));
    expect(result.rejected).toStrictEqual(getFileRejections(files, {
      maxSizeInBytes: maxSizeInBytes
    }));
  });
});