import { faker } from '@faker-js/faker';
import arrayToCsv from '../array-to-csv';
describe('arrayToCsv', function () {
  it.each([undefined, null, '', []])('should return an empty string when value is %p', function (value) {
    var result = arrayToCsv(value);
    expect(result).toBe('');
  });
  it('should return comma separated string of values', function () {
    var values = faker.random.words(10).split(' ');
    var result = arrayToCsv(values);
    expect(result).toStrictEqual(values.join(','));
  });
});