contracts/Greeter.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

// remember to remove unnecessary imports and its use when deploying your smart contract
import "hardhat/console.sol";

contract Greeter {
    event newGreeting(string greeting, address sender);

    string private greeting;

    constructor(string memory _greet) {
        console.log("Deploying a Greeter with greeting:", _greet);
        greeting = _greet;
    }

    function greet() external view returns (string memory) {
        return greeting;
    }

    function setGreeting(string calldata _greeting) external {
        console.log("Changing greeting from '%s' to '%s'", greeting, _greeting);
        greeting = _greeting;
        emit newGreeting(_greeting, msg.sender);
    }
}

contracts/Oracle.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

// currently it only works with node v16
import "redstone-evm-connector/lib/contracts/message-based/PriceAware.sol";

// You can find more details about RedStone oracles here: https://tinyurl.com/redstone-celo-docs

/**
 * @title Oracle
 * @dev Read data from RedStone oracle protocol
 */
contract Oracle is PriceAware {
    /**
     * @dev Calculates amount of CELO tokens equal to a given USD amount
     * @return amount of CELO tokens corresponding to a given USD amount
     */
    function getCELOAmountForUSDAmount(uint256 usdAmount)
        public
        view
        returns (uint256)
    {
        // You can use `getPriceFromMsg` in non-view contract functions as well
        // But you always need to wrap your ethers.js contract instance using RedStone wrapper
        // in your JS interface
        uint256 celoPrice = getPriceFromMsg(bytes32("CELO"));
        return (usdAmount * (10**8)) / celoPrice;
    }

    function isSignerAuthorized(address _receviedSigner)
        public
        view
        virtual
        override
        returns (bool)
    {
        // You can check check evm addresses for providers at: https://api.redstone.finance/providers
        return _receviedSigner == 0x0C39486f770B26F5527BBBf942726537986Cd7eb; // redstone main demo provider
    }
}

contracts/SimpleERC115.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract SimpleERC115 is ERC1155, Ownable {
    using Strings for uint256;

    bool public publicSaleActive;

    uint256 public constant MAX_SUPPLY = 10;

    uint256 public mintPrice = 0.01 ether;

    uint256 private _tokenIds;

    string[MAX_SUPPLY] private _tokenUris;

    modifier whenPublicSaleActive() {
        require(publicSaleActive, "Public sale is not active");
        _;
    }

    constructor() ERC1155("https://www.trashpanda.racoon/metadata/{id}.json") {}

    function addTokenUri(uint256 tokenId, string memory tokenUri)
        external
        onlyOwner
    {
        _tokenUris[tokenId] = tokenUri;
    }

    function uri(uint256 _id) public view override returns (string memory) {
        if (bytes(_tokenUris[_id]).length > 0) {
            return _tokenUris[_id];
        }

        return string(super.uri(_id));
    }

    function startPublicSale() external onlyOwner {
        require(!publicSaleActive, "Public sale has already begun");
        publicSaleActive = true;
    }

    function pausePublicSale() external onlyOwner whenPublicSaleActive {
        publicSaleActive = false;
    }

    function mint(uint256 numNfts) external payable whenPublicSaleActive {
        require(
            _tokenIds + numNfts <= MAX_SUPPLY,
            "Minting would exceed max supply"
        );
        require(numNfts > 0, "Must mint at least one NFT");
        require(
            mintPrice * numNfts <= msg.value,
            "Ether value sent is not correct"
        );

        for (uint256 i = 0; i < numNfts; i++) {
            _mint(msg.sender, _tokenIds, 1, "");
            _tokenIds += 1;
        }
    }

    function mintTo(uint256 numNfts, address toAddress)
        external
        onlyOwner
        whenPublicSaleActive
    {
        require(
            _tokenIds + numNfts <= MAX_SUPPLY,
            "Minting would exceed max supply"
        );
        require(numNfts > 0, "Must mint at least one NFT");

        for (uint256 i = 0; i < numNfts; i++) {
            _mint(toAddress, _tokenIds, 1, "");
            _tokenIds += 1;
        }
    }
}

