pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @notice A simple ERC20 Token implementation that also accepts donation for the project
 */
contract SupportToken is ERC20 {
    uint sentIn;
    address payable owner;

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    constructor() ERC20("Support Token", "STT") {
        /// @notice mint 10000 tokens to the owner
        _mint(msg.sender, 10000e18);
        owner = payable(msg.sender);
        sentIn = 0;
    }

    function acceptDonation(uint amount)
        public
        payable
        returns (bool accepted)
    {
        require(amount == msg.value, "Invalid amount!");

        sentIn += msg.value;

        return true;
    }

    function withdrawChest() public onlyOwner returns (bool) {
        bool success = owner.send(address(this).balance);

        if (success) return true;

        return false;
    }
}

contracts/digesu/account/Account.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

// import "../libs/Context.sol";
// import "../interfaces/IERC20.sol";
import "../interfaces/IAccount.sol";
import "../interfaces/IDigesu.sol";
import "../libs/Utils.sol";
import "../libs/Ownable.sol";
import "../libs/ReentrancyGuard.sol";
import "../libs/Address.sol";

/**
  @title Account: 
    Interactive account is a separate entity distinct fron the owner. 
    They are able to interact with most Digesu's products. Routers interact directly with
    accounts, not the owner. They own power to approve or disapprove transactions.
    Routers read and push information to accounts only on trigger by the owner.
      - An exceptional case is where funds need to be moved to the next person.
      - In such case, the owner must have pay back and approve Router to spend 
         from their account. Usually, Router locks the amount in user's account
         until the need for it arise.
    Note: Routers don't take actions unless triggered by the owner.

  Error Code:
    1. Token not supported.
    2. Trying to deposit zero value.
    3. UnAuthorizedCaller.
*/
contract Account is IAccount, Context, Ownable, ReentrancyGuard {
  using Utils for *;

  // Address of Router
  address payable private router;

  //Manager contract
  address public manager;

  // Address of the Mother branchup
  address private branchUp;

  //Total amount engaged at any time
  uint private engaged;

  // Control contract execution
  bool private _pause;

  // Supported assets
  address[] private supportedAssets;

  // Mapping of added supported assets
  mapping (address=>bool) public added;

  // All Subscriptions
  mapping (uint=>Info) public subscriptionInfo;

  /**@dev Stores balances related to erc20 addresses at anytime
   * both withdrawable and engaged balances.
  */
  mapping (address=>mapping(Balances=>uint)) public balances;

  /**@dev Initializes state variables.
   *  o We set factory as the router with exclusive access to certain 
   *      sensitive functions.
   *  o @param newOwner : i.e the calling EOA is set as the sole owner.
   */
  constructor (address newOwner, address router_) payable {
    router = payable(router_);
    _pause = false;
    manager = payable(_msgSender());
    transferOwnership(newOwner);
  }

  /**@dev
   * Owner cannot withdraw if account is engaged.
   * This type restriction is enforced to avoid replay && DOS attack 
   * by the owner in certain conditions.
   * Both currencies are by default gated.
  */
  modifier syncBalances(address token, uint _amtToWithdraw, uint optionalArg) {
    if(token == address(0)) revert ZeroAddress(token);
    _syncBalances(token, optionalArg);
    uint withdrawable = balances[token][Balances.WITHDAWABLE];
    if(_amtToWithdraw > withdrawable) revert InsufficientFund(
      withdrawable, _amtToWithdraw
    );
    _;
  }

  modifier whenNotPaused() {
    if(_pause) revert ExecutionStopped();
    _;
  }

  /**@dev
   *  Only supported assets are allowed i.e NATIVE or ERC20
   * @param erc20Address Incoming token address
   * Note Address must not be the zero address.
  */
  modifier isSupportedToken(address erc20Address) {
    if(erc20Address == address(0)) revert ZeroAddress(erc20Address);
    if(!IDigesu(_router()).supportedToken(erc20Address)) revert UnsupportedAsset(erc20Address);
    if(!added[erc20Address]) {
      added[erc20Address] = true;
      supportedAssets.push(erc20Address);
    }
    _;
  }

  modifier onlyRouter() {
    require(_msgSender() == _router(), "Account.sol: OnlyRouter");
    _;
  }

  // Fallback
  receive() external payable { 
    revert IDoNotAcceptEtherIFYouForceItLost();
  }

  // /**@dev Returns subscrition detail for the pool at 'poolId'
  //  * Note: Read information from this Account based on 'poolId'.
  //  * @param poolId - Exact pool index.
  //  */
  // function getSubscription(uint poolId) external view returns (Info memory _sub) { _sub = subscriptionInfo[poolId]; }

  // /**@dev
  //  * 
  //  */
  // function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
  
  /**@dev Utility to upgrage to a new account.
   * Note : New account must be approved from the accout manager
   *        before they can be upgraded.
   */
  function rekey(address newAccount) external returns(bool) {
    require(_msgSender() == manager, "NA");
    address[] memory _assets = supportedAssets;
    for (uint i = 0; i < _assets.length; i++) {
      address asset = supportedAssets[i];
      uint bal = IERC20(asset).balanceOf(address(this));
      if(bal > 0) {
        require(IERC20(asset).transfer(newAccount, bal), "Error");
      }
    }
    Address.functionCallWithValue(
      newAccount,
      abi.encodeWithSelector(
        bytes4(keccak256(bytes("accept(address[] memory)"))), 
        _assets
      ),
      address(this).balance
    );
    transferOwnership(manager);
    haltExecution();

    return true;
    // SafeCallAccount.safeTransferData(newAccount, _assets);
  }

  function haltExecution() private  {
    _pause = true;
  }

  // Returns router - Gas saving.
  function _router() internal view returns(address _fetched) { _fetched = router; }

  /**
   * @notice Alternate function to deposit netork asset. 
   * Note Public function, no restriction.
  */
  // function depositNative() external payable { require(msg.value > 0, "2"); }

  /**@dev Synchronizes balances. We update both balances each time this 
      function is called.
      @param token : Address of the ERC20 supported asset.
      @param optionalArg : is any value that should be deducted along with the actual
                            value sent in call such as makerFee or Router commission.
   */
  function _syncBalances(address token, uint optionalArg) private {
    uint _balInUse = balances[token][Balances(1)];
    balances[token][Balances.WITHDAWABLE] = token == address(this)? address(this).balance : IERC20(token).balanceOf(address(this));
    balances[token][Balances.WITHDAWABLE] -= ( _balInUse + optionalArg );
  }

  /**
   * @notice Utility to deposit ERC20 token 
   * Note Public function, no restriction. Only that token must be supported.
   * @param erc20Address Address of the depositing token.
   * @param amount Deposit amount.
   * @return null
  */
  function depositERC20Token(address erc20Address, uint256 amount) 
    external
    whenNotPaused
    isSupportedToken(erc20Address) 
    returns(bool) 
  {
    IERC20(erc20Address).transferFrom(_msgSender(), address(this), amount).assertUnchained('Transfer Failed');
    _syncBalances(erc20Address, 0);

    return true;
  }

  // /**
  //  * @notice Utility to withdraw network asset.
  //  * Note Restricted
  // */
  // function withdrawNativeOnlyRouter(
  //   uint amount, 
  //   uint fee, 
  //   address to, 
  //   address feeTo
  //   ) external onlyRouter nonReentrant syncBalances(address(this), amount, fee){
  //   (bool j,) = feeTo.call{value: fee}("");
  //   (bool i,) = to.call{value: amount}("");

  //   require(i && j, "Transfered failed");
  // }

  // function withdrawNativeOnlyOwner(uint amount) 
  //   public
  //   onlyOwner
  //   syncBalances(address(this), amount, 0)
  // {
  //   payable(owner()).transfer(amount);
  // }

  /**
   * @notice Get the balance ERC20 token 
   * Note Public function, no restriction. Only that token must be supported.
   * @param erc20Address Address of the depositing token.
   * @return _balances 
  */
  function erc20Balances(address erc20Address) external view returns(uint256 _balances) {
    require(added[erc20Address], "NotSupported");
    return _balances = IERC20(erc20Address).balanceOf(address(this));
  }

  /**
   * @notice Get the balance ERC20 token 
   * Note Public function, no restriction. Only that token must be supported.
  */
  function withdrawERC20TokenOnlyOwner(
    address erc20Address, 
    uint amount
  ) 
    public
    onlyOwner
    whenNotPaused
    isSupportedToken(erc20Address) 
  {
    _withdraw(erc20Address, owner(), amount, 0);
  }

  function _withdraw(
    address asset, 
    address to, 
    uint amount,
    uint optArg
  ) private nonReentrant syncBalances(asset, amount, optArg) {
    IERC20(asset).transfer(to, amount);
    _updateBalancesInUse(asset, amount, true);
  }

  function withdrawRouterOnly(
    address token, 
    address to,
    address feeTo,
    uint amount, 
    uint fee) external whenNotPaused onlyRouter  returns(bool) {
      _withdraw(token, to, amount, fee);
      _withdraw(token, feeTo, fee, 0);

      return true;
  }

  /**
   * @notice Returns subscription information for owner relating to poolId
   * Note For every pool the owner has subscribed to, they can fetch data relating to each band.
   * @param poolId index of band.
   * @return Info
  */
  function getSubscriptionInfo(uint poolId) external view returns(Info memory) {
    return subscriptionInfo[poolId];
  }

  function setStatus(
    uint poolId,
    bool isAdmin, 
    bool isMember
  ) external whenNotPaused onlyRouter returns(bool) {
   _setStatus(poolId, isAdmin, isMember);
   
    return true;
  }

  function _setStatus(
    uint poolId,
    bool isAdmin, 
    bool isMember
  ) private {
    subscriptionInfo[poolId].isAdmin = isAdmin;
    subscriptionInfo[poolId].isMember = isMember;
  }


  /**@dev Router updates balance in use. 
   * Note Callable only by the Mother branchup.
  */
  function updateBalancesInUse(address asset, uint value, bool reduce) 
    external 
    whenNotPaused 
    onlyRouter returns(bool) 
  {
    _updateBalancesInUse(asset, value, reduce);
    return true;
  }

  function _updateBalancesInUse(address asset, uint value, bool reduce) private {
    reduce? balances[asset][Balances.INUSE] -= value : balances[asset][Balances.INUSE] += value;
  }

  function getSpendableBalance(address token) external view returns(uint _withdrawable, uint _engaged, uint _mostRecent) {
    _withdrawable = balances[token][Balances.WITHDAWABLE];
    _engaged = balances[token][Balances.INUSE];
    _mostRecent = token == address(0) ? IERC20(token).balanceOf(address(this)) : address(this).balance;
  }

  function approve(address erc20Address, uint amount)
    public
    whenNotPaused
    onlyOwner 
    isSupportedToken(erc20Address) {
      IERC20(erc20Address).approve(_router(), amount);
  }

  function updateTurnTime(uint poolId) external whenNotPaused returns(bool) {
    subscriptionInfo[poolId].turnTime = block.timestamp + 1 hours;
    return true;
  }
  
  function initializeInfo(
    Info memory info, 
    bool lock,
    bool reduceBalance,
    uint poolId,
    uint value,
    address asset,
    address to
  ) external whenNotPaused returns(bool) {
    subscriptionInfo[poolId] = info;
    if(lock) {
      _updateBalancesInUse(asset, value, reduceBalance);
    } else {
      _withdraw(asset, to, value, 0);
    }

    return true;
  }

  function clearSubscription(uint poolId) 
    external 
    whenNotPaused 
    onlyRouter returns(bool) {
      delete subscriptionInfo[poolId];
      return true;
  }

  function split(
    uint poolId,
    address asset,
    address[] memory members,
    address closeTo,
    uint unitAmount, 
    uint _balance) external whenNotPaused onlyRouter returns(bool) 
    {
      _setStatus(poolId, false, false);
      uint _bal = _balance;
      for(uint i = 0; i < members.length; i++) {
        _bal -= unitAmount;
        _withdraw(
          asset, 
          members[i], 
          unitAmount,
          0
        );
      }
      if(_bal > 0) _withdraw(  asset,   closeTo,   unitAmount,  0);

      return true;
  }


}

contracts/digesu/account/AccountManager..content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "../interfaces/ICommon.sol";
import "./Account.sol";
import "../interfaces/IAccount.sol";
import "../interfaces/IAccountManager.sol";

/**@title AccountManager: standalone contract that manages account creation, 
   deletion, including read and write data.

   Author: Bobeu
   Github: https://github.com/bobeu
 */
contract AccountManager is IAccountManager, ICommon, Ownable {
  uint public accountsCounter;

  // Acount creation fee
  uint private accountCreationFee;

  //Address to receive fee
  address private feeTo;

  // Digesu factory contract address
  address public factory;

  //Approvals to upgrade to a new account
  mapping(address=>bool) public approvals;

  ///@dev Users mapped to records
  mapping (address=>Accounts) private accounts;

  // constructor () {}

  modifier scrutinizeAccount(address who, bool value, string memory errorMessage) {
    require(hasAccount(who) ==  value, errorMessage);
    _;
  }
  
  /**@dev Return true if "who's" account status is not empty
       otherwise false.
   */
  function hasAccount(address who) public view override returns (bool) {
    return accounts[who].active != address(0);
  }

  // Set account creation fee : Should be called only by the multisig account
  function setAccountCreationFee(uint newFee) public onlyOwner {
    accountCreationFee = newFee;
  }
  
  // Returns account for 'who'
  function getAccount(address who) external view returns (address) { 
    return accounts[who].active; 
  }
  
  /**@dev Launches new INTERACTIVE account for 'who'.
   * If fee is applied, msg.value must meet minimum creationFee otherwise, creation fail.
   * Owner can prefund account at construction if they wish do so. They only have to increase the msgValue
   * above minimum accountCreationFee.
  */
  function createAccount(uint initialAccountBalance, address who)
    external 
    payable
    scrutinizeAccount(who, false, "User exist")
    returns(address newAlc) 
  {
    require(msg.value >= accountCreationFee && feeTo != address(0), "3");
    accountsCounter ++;
    newAlc = address(new Account{value: initialAccountBalance}(who, _getFactory()));
    accounts[_msgSender()].active = newAlc;

    emit AccountLaunched(newAlc, _msgSender());
  }

  // Reset address to receive fee : only by owner account.
  function changeFeeTo(address newFeeTo) public onlyOwner {
    if(newFeeTo == address(0)) revert ZeroAddress(newFeeTo);
    feeTo = newFeeTo;
  }

  //Reset factory address : onlyOwner
  function setFactory(address newFactory) public onlyOwner {
    if(newFactory == address(0)) revert ZeroAddress(newFactory);
    factory = newFactory;
  }

  /**@dev Upgrade to a new account.
   * @param newAccount : New account to upgrade to.
   * Note : newAccount must be approved prior to this call
   */
  function rekeyAccount(address newAccount) 
    external
    scrutinizeAccount(_msgSender(), true, "User not exist")
    returns(bool) 
  {
    if(!approvals[newAccount]) revert AccountNotApproved();
    address oldAlc = accounts[_msgSender()].active;
    require(factory != address(0) && _msgSender() == factory, "Denied");
    if(!IAccount(oldAlc).rekey(newAccount)) revert SomethingWentWrong();

    emit Rekeyed(oldAlc, newAccount);
    return true;
  }

  /**@dev Approves new account for upgrade
   * Note : Same utility can also disapprove
  */
  function setApproval(address newAccount, bool _approval) public onlyOwner {
    if(approvals[newAccount] == _approval) revert StatusAlreadyUpdated();
    approvals[newAccount] = _approval;
  }

  ///@dev Deactivate account for 'target'
  function deactivateAccount(address target) 
    public
    onlyOwner
    scrutinizeAccount(target, true, "User is deactivated")
  {
    Accounts memory alcs = accounts[target];
    accounts[target] = Accounts(alcs.deactivated, alcs.active);
  }

  ///@dev Aactivate account for 'target'
  function activateAccount(address target) 
    public
    onlyOwner
    scrutinizeAccount(target, false, "User is deactivated")
  {
    Accounts memory alcs = accounts[target];
    if(alcs.deactivated == address(0)) revert TargetHasNoAccount();
    accounts[target] = Accounts(alcs.deactivated, alcs.active);
  }

  function _getFactory() internal returns(address _fact) {
    factory = _fact;
  }
}

contracts/digesu/Digesu.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "./DigesuLib.sol";


/**AUTHOR: ISAAC JESSE 
 * GITHUB: "https://github.com/bobeu"
 * LINKEDIN: "https://linkedin.com/
*/


/** 
  Digesu
    ======
    A multi-peer finance structure that allows many parties come together, contribute money
    in equal amount for the purpose of financing themselves in a rotational manner. All the 
    participant is lender and borrower same time. Each beneficiary pays back with very minimal
    maker rate that's almost insignificant ranging between as little as 0.1 to 5%. The maker 
    rate being an amount charged in favor of the platform.

    For every successful circle, all the participant is entitled to farming reward in QFT, with 
    which they can make more profit. We also bountily reward best performing band every 3 months. 

      Example: A 3-man band (A, B, C) created with unit amount 1 ETH.
              - 'A' launches the band with a commitment of 1 ETH, becomes the admin
              - 'A' is added to slot 1, and a trustee is created for the band.
              - 1 ETH is forwarded to trustee.
              - 'B' joins with commitment of 1 ETH, added to slot 2.
              - 'C' joins with commitment of 1 ETH, added to slot 3.
              - There is 3 ETH locked with Trustee, and "A's" time to withdraw is now.
              - Join function is disabled, and 'GetFinance' is unlocked.
              - 3 ETH is available to 'B' to withdraw.
              - 'B's wallet is screened for enough collateral (QFT).
                o If true, 3 ETH is sent to 'B' otherwise operation failed.
                o If time for 'B' to withdraw has passed, any member of the band
                  who is the current caller will replace "B's" position, and claim
                  the fund.
                o Assume 'C' calls, "C's" position is swapped with "B's".

                    CIRCUIT BREAK
                    -------------
                    o There is a circuit break when any member of such band explicitly call the roundUp().
                    o This can only be done when all the participants has successfully 'GETFINANCED'.

                PAYBACK: 
                ======
                o Any member that 'GETFINANCE' has a payback period which was initially specified 
                  by the admin.
                o If the payback period elapsed, nothing is done, until someone calls to liquidate
                  the defaulter.
                o If after time elapsed, the beneficiary calls, they're surcharge with 5% of the 
                  collateral balance (in token value). The penalty is charged in favor of the next
                  on the queue to 'GETFINANCE', and his/her account is credited. The balance being
                  the amount left after 5% deduction is released to the owner.
                o If the beneficiary stays within expected repayment time (ERP), the above shall not
                  apply. Their collateral balance is unlocked immediately.
                
                LIQUIDATION
                ==========
                o Anyone (either a member or outsider) can liquidate the defaulter. When this happens,
                  the liquidator absorbs the current debt (exact amount or greater must be sent along
                  with the call). They absorbs fully the profile of the defaulter including the collateral
                  balances. The circle then continues.

     ERROR CODE
     ==========
  1. Function not available.
  2. Value is below minimum.
  3. Not a member.
  4. Pool not ready.
  5. No debt found.
  6. User is yet to default payment.
  7. Address is empty.
  8. Price is empty.
  9. Members exceed limit.
  10. Already a member of this band.
  11. Value out of bound.

    1. Amount exists
  2. Not an Owner
  3. Insufficient balance
  4. Out of bound.
  5. Pool is filled.
  6. Function locked.
  7. User not a member in this band.
  8. Value less than expected.
  9. Invalid pool id.
  10. User already getFinance.
  11. User is yet to default payment.
  12. Address is empty.
  13. Invalid positioning.
  14. Anomally detected: User not paid;
  15. Cannot cancel at this time. Pool already more than 1.
  16. All members yet to GetFinance.
  17. Cannot have admin in multiple places.
  18. User cannot replicate in a band.
  19. Invalid input.
  20. Operation failed.
 */

contract Digesu is IDigesu, Currency, FuncHandler, Pausable, Ownable {
  using Utils for bool;
  using DigesuLib for *;

  Data private data;

    // Creation fee
  uint public creationFee;

  // Minimum amount that can be contributed
  uint public minimumPoolAmount;

  IAccountManager public manager;

    /**Other statistical data - Read-only
      o Total Values contributed todate;
      o Total subscribers;
  */
  CR public cr;

  modifier validateId(uint poolId) {
    data.pools.comparePoolSize(poolId);
    _;
  }
  
  // Only when user is a member.
  modifier memberStatus(uint poolId, address who, bool value, Mode mode) {
    data.pools.assertIsMember(poolId, who, value);
    _;
  }
  
  /**
    @dev Compares number of participants.
      @param quorum - Number that should make up the required participants.
  */
  modifier validateQuorum(uint8 quorum) {
    if(quorum == 0) revert InvalidInput();
    if(quorum > type(uint8).max) revert InvalidInput();
    // bool(quorum > 0).assertChained2(quorum <= type(uint8).max, "9");
    _;
  }

  /**@dev Scrutinize account from _msgSender if it exist
   * or not. 
   * Note: Execution depends on the 'value'
   */
  modifier checkIfAccountExist(address who, bool value) {
    _check(value, who);
    _;
  }

  modifier checkIfAddressesAreSet(address _token, address feeTo,address _manager) {
    require(_token != address(0) && feeTo != address(0) && _manager != address(0), "Addresses not set");
    _;
  }

  modifier validateList(address[] memory members, Mode mode) {
    if(mode == Mode.STRICT) {
      if(members.length <= 1) revert InsufficientQuorum();
      // require(members.length > 1, "19");
    }
    require(members[0] ==_msgSender(), "19");
    _;
  }

  function setParams(
    address _token, 
    uint _minimumPoolAmount, 
    address feeTo,
    address _manager
  ) 
    public onlyOwner  checkIfAddressesAreSet( _token,  feeTo, _manager )
  {
    data.pcd.token = _token;
    data.pcd.feeTo = feeTo;
    minimumPoolAmount = _minimumPoolAmount;
    manager = IAccountManager(_manager);
  }

  //Fallback - Forward unsolicited Ether.
  receive() external payable {
    Address.sendValue(payable(data.pcd.feeTo), msg.value);
  }

  //Return account belonging to 'who';
  function _account(address who) internal view returns(address alc) {
    alc = manager.getAccount(who);
  }

  /**@dev If 'value' is true, 'who' must not already own an account
   * otherwise, 'who' must own an account before now.
   */
  function _check(bool value, address who) internal view {
    value? require(_account(who) == address(0), "1") : require(_account(who) != address(0), "2");
  }

  /**
    @dev Launches a public band - Native currency i.e ETH or Platform currency.
      @param quorum - Required number of participants to form a band. 
      @param durationInDays - The maximum time limit (from when the turn time begins) with which a participant
                                will take custody of the loan before repayment.
      @param colCoverageRatio - Collateral factor - determinant of the amount of collateral to require of gFer.
                                  This is expressed as a multiple index of total loanable amount.
      @param amount - Unit contribution.
      @param asset - address of the ERC20 standard asset to use.
                Note: asset must be supported by digesu.
  */
  function createPublicPool(
    uint8 quorum, 
    uint8 durationInDays, 
    uint16 colCoverageRatio, 
    uint amount,
    address asset) 
      external
      whenNotPaused
      validateQuorum(quorum)
      checkIfAccountExist(_msgSender(), false) 
      onlySupportedToken(asset)
      // checkIfAddressesAreSet(data.pcd.token,  data.pcd.feeTo, address(manager) )
      returns (bool)
  {
      address[] memory addrs = new address[](1);
      addrs[0] = _msgSender();
      (uint newPoolId) = data.createPublicPool(P1
        (
          uint8(quorum),
          durationInDays,
          colCoverageRatio,
          amount,
          addrs,
          asset
        ),
        _account,
        _unlock
      );
    require(data.pcd.token != address(0) && data.pcd.feeTo != address(0) && address(manager) != address(0), "Addresses not set");
    emit BandCreated (newPoolId, data.pools._fetchPoolData(newPoolId));
    return true;
  }

   /**
    @dev Launches a public band - Native currency i.e ETH or Platform currency.
      @param durationIndays - The maximum time limit (from when the turn time begins) with which a participant
                                will take custody of the loan before repayment.
      @param colCoverateRatio - Collateral factor - determinant of the amount of collateral to require of gFer.
                                  This is expressed as a multiple index of total loanable amount.
      @param members- List of expected members.
      @param asset - Asset's contract address.
      @param amount - Contribution amount.
  */
  function createPrivatePool(
    uint8 durationIndays, 
    uint16 colCoverateRatio, 
    address[] memory members, 
    address asset,
    uint amount) 
      external 
      whenNotPaused
      validateQuorum(uint8(members.length))
      checkIfAccountExist(_msgSender(), false) 
      onlySupportedToken(asset)
      validateList(members, Mode.STRICT)
      returns(bool)
  {
    uint newPoolId = data.createPrivatePool(P1
      (
        uint8(members.length),
        durationIndays,
        colCoverateRatio,
        amount,
        members,
        asset
      ),
      _account,
      _check,
      _unlock
    );
    emit BandCreated (newPoolId, data.pools._fetchPoolData(newPoolId));
    
    return true;
  }

  /**
    @dev Updates minimum amount contribution amount
      Note: Only Owner have the privilege to call this function.
  */
  function updateMinPoolAmount(uint256 newAmount) public onlyOwner {
    minimumPoolAmount = newAmount;
  }
  
  /**@dev Add new member.
      @param poolId : Band index.
        Note: We set up a trustee to hold all funds in the pool at poolId in trust 
                  for all members.
   */
  function joinABand(uint poolId) 
    external
    whenNotPaused
    validateId(poolId)
    checkIfAccountExist(_msgSender(), false)
    memberStatus(poolId, _account(_msgSender()), false, data.pools._getMode(poolId))
    checkFunctionPass(poolId, FuncTag.ADD)
    returns(bool)
    {
      (Pool memory _p, address alc) = data.pools.joinABand(poolId, _account);
      cr.totalValueContributed += _p.uint256s.unit;
      cr.subscribers ++;
      if(_p.mems.length == _p.uints.quorum) {
        _lock(poolId, FuncTag.ADD);
        _unlock(poolId, FuncTag.GET);
        if(!IAccount(alc).updateTurnTime(poolId)) revert Failed();
        // require(IAccount(alc).updateTurnTime(poolId), "Failed");
      }

      emit Joined(poolId, alc, _p.uint256s.unit);

      return true;
  }

  
  /**@dev Members of a pool can pick up the contributed fund when certain coditions
          are met.
      @param poolId : Band's Id which caller belong to.
        Note:
  */
  function getFinance(uint poolId) 
    external
    whenNotPaused 
    validateId(poolId)
    checkIfAccountExist(_msgSender(), false) 
    memberStatus(poolId, _account(_msgSender()), true, data.pools._getMode(poolId))
    checkFunctionPass(poolId, FuncTag.GET)
    returns(bool)
  {
   (address valueTo, uint position) = data.getFinance(ICommon.CreateParam(poolId, _lock, _account, _getQFTPriceInETH));

    emit GetFinanced(poolId, position, valueTo);
    return true;
  }

   /**
    @dev Utility to payback borrowed fund.
      @param poolId : Pool number.
      Note: We are only concerned about who is to pay back and not 
              who is paying back meaning any of the members can pay 
              on behalf of another.
   */
  function payback(uint poolId) 
    external 
    whenNotPaused
    checkIfAccountExist(_msgSender(), false)
    validateId(poolId)
    memberStatus(poolId, _account(_msgSender()), true, data.pools._getMode(poolId))
    returns(bool)
  {
    (GCB memory gcb, address alc) = data.payback(
      poolId, 
      _account, 
      _lock, 
      _unlock
    );
    CR memory _cr = cr;
    gcb.cr = _cr;
    Utils.unlockCollateral(gcb);

    emit Payback(poolId, gcb.cr2.valueContributed, alc);
    return true;
  }

  /**
  @dev Liquidates defaulter.
    Note: The expected repayment time for last paid must have passed.
  */
  function liquidate(uint poolId) 
    external 
    whenNotPaused
    checkIfAccountExist(_msgSender(), false)
    returns(bool)
  {
    (address liq, uint amount) = data.liquidate(poolId, _msgSender(), _account);
    emit Payback(poolId, amount, liq);

    return true;
  }

  // See SubMain._enquireLiquidation(pid);
  function enquireLiquidation(uint poolId) external view returns(Liquidation memory)  { 
    return data.pools.enquireLiquidation(poolId); 
  }

   //See SubMain.sol
  function roundUp(uint poolId) 
    external
    whenNotPaused 
    checkFunctionPass(poolId, FuncTag.COMPLETE)
    validateId(poolId)
    checkIfAccountExist(_msgSender(), false)
    memberStatus(poolId, _account(_msgSender()), true, data.pools._getMode(poolId))
    returns(bool)
  { 
    ICommon.Pool memory pool = data.roundUp(poolId);
    emit ICommon.RoundUp(poolId, pool);

    return true;
  }

  ///@dev Pauses contract
  function pause() public onlyOwner {
    _pause();
  }

  ///@dev Unpauses contract
  function unpause() public onlyOwner {
    _unpause();
  }

  ///@dev Updates penalty fee.
  function updatePublicData(
    address feeTo,
    address token,
    uint8 penFee,
    uint8 makerFee
  ) public onlyOwner 
  {
    data.updatePublicData( feeTo, token, penFee, makerFee );
  }

  // See SubMain.cancelBand(pid, who);
  function cancelBand(uint poolId) 
    external
    whenNotPaused
    validateId(poolId)
    checkIfAccountExist(_msgSender(), false)
    memberStatus(poolId, _account(_msgSender()), true, data.pools._getMode(poolId))
    returns(bool)
  {
    (uint value, address alc) = data.cancelBand(poolId, _account);
    emit Cancellation(poolId, value, alc);
    
    return true;
  }

  // See SubMain._getPools()
  function allPools() public view returns(Pool[] memory) {
    return data.getPools();
  }

  function setSupportedTokens(address newToken) public override onlyOwner {
    super.setSupportedTokens(newToken);
  }

  function supportedToken(address _token) external view returns(bool) {
    return _supportedToken(_token);
  }

  // Get the token ptice
  function _getQFTPriceInETH() internal pure returns (uint _price) {
    _price = 1e15 wei; // ================================================> We use oracle here
  }

}

contracts/digesu/DigesuLib.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "./interfaces/IDigesu.sol";
import "./interfaces/ICommon.sol";
import "./libs/Utils.sol";
import "./peripherals/FuncHandler.sol";
import "./account/AccountManager.sol";
import "./peripherals/Currency.sol";
import "./interfaces/IAccountManager.sol";

/**
  ERROR CODE
  ==========
  1. Amount exists
  2. Not an Owner
  3. Insufficient balance
  4. Out of bound.
  5. Pool is filled.
  6. Function locked.
  7. User not a member in this band.
  8. Value less than expected.
  9. Invalid pool id.
  10. User already getFinance.
  11. User is yet to default payment.
  12. Address is empty.
  13. Invalid positioning.
  14. Anomally detected: User not paid;
  15. Cannot cancel at this time. Pool already more than 1.
  16. All members yet to GetFinance.
  17. Cannot have admin in multiple places.
  18. User cannot replicate in a band.
  19. Invalid input.
  20. Operation failed.
 */

struct Data {
  //Other miscellaneous data. 
  IDigesu.PublicData pcd;

  //Pool container
  ICommon.Pool[] pools;
    /**
  @dev Tracks unit contribution.
    @notice User cannot launch a pool with amount 
              already active until such band is filled or cancelled. 
  */
  mapping(uint256 => bool) amountExist;
}

library DigesuLib {
  using Utils for *;

  ///@dev Checks if @param who { caller } belongs to pool poolId
  function assertIsMember(ICommon.Pool[] storage self, uint poolId, address who, bool value) internal view {
    uint pos = _fetchPosition(poolId, who);
    bool isMember = self[poolId].mems[pos] == who;
    bool(isMember == value).assertUnchained("7");
  }

  /**@dev Returns tracker Id
      @param poolId - Group index.
   */
  function _getTracker(ICommon.Pool[] storage self,  uint poolId) internal view returns (uint8 _return) {
    _return = uint8(self[poolId].mems.length);
  }

  ///@dev Returns all uint256s related data in pool at poolId.
  function _fetchPoolData(ICommon.Pool[] storage self, uint poolId) internal view returns (ICommon.Pool memory _return) {
    _return = self[poolId];
  }

  function _msgSender() internal view returns(address _sender) {
    _sender = msg.sender;
  }

  ///@dev Returns total pool
  function getPools(Data storage self) public view returns (ICommon.Pool[] memory) {
    return self.pools;
  }

  function comparePoolSize(ICommon.Pool[] storage self, uint poolId) internal view {
    bool(poolId < self.length).assertUnchained("9");
  }

  ///@dev Generates new Id for new pool
  function _generateGroupIndex(ICommon.Pool[] storage self) internal view returns (uint _return) {
    _return = uint(self.length);
  }

  //@dev Adds new member
  function _addNewMember(ICommon.Pool[] storage self, uint poolId, address who) private returns( uint position) {
    // tracker = _getTracker(poolId);
    position = self[poolId].mems.length;
    self[poolId].mems.push(who);
    // if(who == _account(_msgSender())) pools[poolId].uints.tracker ++;
  }

  function _createPool(
    Data storage self, 
    ICommon.P1 memory p1, 
    ICommon.Mode mode, 
    uint poolId,
    function (uint, ICommon.FuncTag) internal _unlock
    ) private {
      bool(p1.duration > 0).assertChained3(p1.members.length > 0, p1.duration < type(uint8).max, "4");
      self.pools.push();
      self.pools[poolId].uints = ICommon.Uints(mode, p1.quorum, 0, p1.ccr, p1.duration * 1 days);
      self.pools[poolId].uint256s =  ICommon.Uint256s(p1.value, 0, p1.value);
      self.pools[poolId].addrs = ICommon.Addresses(p1.asset, address(0));
      _unlock(poolId, ICommon.FuncTag.ADD);
      // _unlock(poolId, FuncTag.ADD);
  }

  /**@dev Launches a new public band
      @param p1 : Encapsulates parameters required by this function.
    Note: We explicitly added the band creator to 2nd position.
   */
  function createPublicPool( 
    Data storage self, 
    ICommon.P1 memory p1,
    function (address) internal view returns(address) _account,
    // function (bool, address) internal view _check,
    function (uint, ICommon.FuncTag) internal _unlock
    // function (address, address, uint) internal _screenBalance
  )
    internal
    returns (uint poolId)
  {
    address alc = _account(p1.members[0]);
    self.amountExist[p1.value].assertNotUnchained("1");
    _screenBalance(p1.asset, alc, p1.value);
    poolId = _generateGroupIndex(self.pools);
    bool(p1.duration > 0).assertChained2(p1.duration < type(uint16).max, "4");
    _createPool(self, p1, ICommon.Mode(0), poolId, _unlock);
    _initializeAccount(self.pools, alc, p1.asset, address(0), true, poolId, true, true, p1.value);
    
  }

  /**@dev Launches a new private band
      @param p1 : Encapsulates parameters required by this function.
      Note: Caller must correspond to address at position 2 in the address array.
   */
  function createPrivatePool(
    Data storage self, 
    ICommon.P1 memory p1,
    function (address) internal view returns(address) _account,
    function (bool, address) internal view _check,
    function (uint, ICommon.FuncTag) internal _unlock
  ) 
    internal
    returns (uint poolId) 
  {
    _screenBalance(p1.asset, _account(_msgSender()), p1.value);
    poolId = _generateGroupIndex(self.pools);
    _createPool(self, p1, ICommon.Mode(1), poolId, _unlock);

    for (uint i = 0; i < p1.members.length; i++) {
      bool isAdmin = true;
      bool reduce = false;
      uint value = p1.value;
      address alc = _account(p1.members[i]);
      bool(alc != address(0)).assertUnchained("17");
      if(i > 0) {
        isAdmin = false;
        _check(false, alc);
        value = 0;
        bool(alc != _account(_msgSender())).assertUnchained("17");
      }
      _initializeAccount(self.pools, alc, p1.asset, address(0), true, poolId, isAdmin, reduce, value);
    }
  }

  function _initializeAccount(
    ICommon.Pool[] storage self,
    address alc, 
    address token,
    address transferTo,
    bool lock, 
    uint poolId, 
    bool isAdmin,
    bool reduce,
    uint value
  ) private {
    poolId.safeInitializeInfo(
      ICommon.Info(_addNewMember(self, poolId, alc), 0, 0, 0, 0, 0, isAdmin, true, false), 
      lock,
      reduce,
      IAccount(alc),
      value,
      token,
      transferTo
    );
  }

  function _screenBalance(address token, address alc, uint value) private view {
    (, uint _engaged, uint _mostRecent) = IAccount(alc).getSpendableBalance(token);
    require(_mostRecent > _engaged &&  (_mostRecent - _engaged) >= value , "3");
  }

  /**@dev
    Internal: Instantiates a new band.
   */
  function joinABand(
    ICommon.Pool[] storage self, 
    uint poolId,
    function (address) internal view returns(address) _account
  )
    internal
    returns (ICommon.Pool memory _pool, address alc) 
  {
    ICommon.Pool memory pool = _fetchPoolData(self, poolId);
    alc = _account(_msgSender());
    _screenBalance(pool.addrs.asset, alc, pool.uint256s.unit);
    bool(pool.mems.length < pool.uints.quorum).assertUnchained("5");
    
    if (pool.uints.mode == ICommon.Mode.STRICT) {
      bool(pool.mems[_fetchPosition(poolId, alc)] == alc).assertUnchained("7");
    }
    _initializeAccount(
      self,
      alc,
      pool.addrs.asset,
      pool.mems[0],
      false,
      poolId,
      false,
      false, // 'reduce' is really immeterial here
      pool.uint256s.unit
    );
    require(IAccount(pool.mems[0]).updateBalancesInUse(pool.addrs.asset, pool.uint256s.unit, true), "Sub: Failed");

    self[poolId].uint256s.currentPool += pool.uint256s.unit;
    _pool = _fetchPoolData(self, poolId);
  }

  function _getMode(ICommon.Pool[] storage self, uint poolId) internal view returns(ICommon.Mode mode) {
    mode = _fetchPoolData(self, poolId).uints.mode;
  }

   ///@dev Returns current timestamp (unix).
  function _now() internal view returns (uint) {
    return block.timestamp;
  }

  function _fetchPosition(uint poolId, address alc) internal view returns(uint pos) {
    pos = IAccount(alc).getSubscriptionInfo(poolId).position;
  } 

  ///@dev Modifies member's information 
  function _updateMemberData(
    Data storage self, 
    ICommon.UpdateParam memory upr,
    function (address) internal view returns(address) _account
  ) 
    private 
    returns (address valueTo, uint position) 
  {
    self.pools[upr.poolId].uint256s.currentPool = 0;
    // address _actual = _account(_msgSender());
    valueTo = upr.expected;
    
    ICommon.Info memory exp = IAccount(valueTo).getSubscriptionInfo(upr.poolId);
    position = exp.position;
    if (_account(_msgSender()) != upr.expected) {
      if (_now() > exp.turnTime + 1 hours) {
        assertIsMember(self.pools, upr.poolId, _account(_msgSender()), true);
        valueTo = _account(_msgSender());
        exp = _swapPosition(valueTo, upr.pool.addrs.asset, upr.poolId, exp, valueTo);
        position = exp.position;
      }
    }
    bool reduce;
    uint val = upr.pool.uint256s.currentPool;

    if(valueTo == upr.pool.mems[0]) {
      reduce = true;
    } else {
      val = upr.owings + upr.makerFee;
      upr.pool.addrs.asset.safeWithdrawRouterOnly(
        IAccount(upr.pool.addrs.lastPaid),
        valueTo, 
        self.pcd.feeTo, 
        upr.pool.uint256s.currentPool, 
        upr.makerFee
      );
    }

    upr.poolId.safeInitializeInfo(
        ICommon.Info(exp.position, _now() + upr.pool.uints.duration, 0, upr.owings, 0, upr.colBals, exp.isAdmin, true, true),
        true,
        reduce,
        IAccount(valueTo),
        val,
        upr.pool.addrs.asset,
        address(0)
      );
    
    self.pools[upr.poolId].uints.selector ++;
  }

  function _swapPosition(
    address actual,
    address asset,
    uint poolId,
    ICommon.Info memory expected,
    address valueTo
  ) private 
    returns(ICommon.Info memory _expected) 
  {
    ICommon.Info memory act = IAccount(actual).getSubscriptionInfo(poolId);
    uint posActual = act.position;
    act.position = expected.position;
    expected.position = posActual;
    _expected = act;
    
    require(
      IAccount(valueTo).initializeInfo(
        expected, 
        true,
        false,
        poolId, 
        0, 
        asset,
        address(0)
      ), 
    "20"
    );
  }

  /**
    @dev Penalizes an user for late repayment
          @notice Defaulter is surcharged.
                  Penalty is credited to the account of next member to GH.
  */
  function _computePenalty(IDigesu.PublicData storage self, uint poolId, address user) internal view returns (uint256) {
    if (self.penFee == 0) return 0;

    return Utils.mulDivOp(IAccount(user).getSubscriptionInfo(poolId).colBals, self.penFee);
  }

  /**@dev Can update the penalty rate. Should be restricted to the owner or authorized admin. */
  function updatePenFee(IDigesu.PublicData storage self, uint8 newRate) internal {
    require(newRate <= type(uint8).max, "Invalid rate");
    self.penFee = newRate;
  }

  /**Utility to get finance
    @param self : Storage
    @param crp : Parameters.
  */
  function getFinance(
    Data storage self,
    ICommon.CreateParam memory crp
    // uint poolId,
    // function (uint, ICommon.FuncTag) internal _lock,
    // function (address) internal view returns(address) _account,
    // function () internal returns(uint) _getQFTPriceInETH
  ) 
    internal
    returns(address valueTo, uint position)
  {
    crp._lock(crp.poolId, ICommon.FuncTag.GET);
    ICommon.Pool memory pool = _fetchPoolData(self.pools, crp.poolId);
    if(pool.allGh == pool.uints.quorum) revert ICommon.AllMemberIsPaid();
    bool(pool.uint256s.currentPool >= ( pool.uint256s.unit * pool.uints.quorum)).assertUnchained("4");
    (uint mFee, uint owings) = ICommon.CMF(pool.uint256s.currentPool, self.pcd.makerFee).computeMakerFee();
    (valueTo, position) = _updateMemberData(
        self, 
        ICommon.UpdateParam(
        pool.mems[pool.uints.selector], 
        crp.poolId,
        owings,
        mFee,
        ICommon.CC(
            self.pcd.token, 
            crp._account(_msgSender()), 
            pool.uints.ccr, 
            crp._getQFTPriceInETH(), 
            pool.uint256s.currentPool
          ).computeCollateral(),
        pool
      ),
      crp._account
    );
    self.pools[crp.poolId].allGh ++;
  }

  ///@dev Returns info if current beneficiary has defaulted otherwise an empty struct is returned.
  function enquireLiquidation(ICommon.Pool[] storage self, uint poolId) internal view returns (ICommon.Liquidation memory) {
    ICommon.Pool memory _p = _fetchPoolData(self, poolId);
    ICommon.Info memory info = IAccount(_p.addrs.lastPaid).getSubscriptionInfo(poolId);
    return _now() > info.payDate ? ICommon.Liquidation(
      info.position, 
      _p.addrs.lastPaid,
      info.payDate,
      info.owings, 
      info.colBals
    ) : ICommon.Liquidation(0, address(0), 0, 0, 0);
  }


  /**
    @dev Liquidates defaulter.
          - If the current beneficiary defaults, they're liquidated.
          - Their collateral balances is forwarded to the liquidator.

  */
  function liquidate(
    Data storage self, 
    uint poolId, 
    address who,
    function (address) internal view returns(address) _account
  ) 
    internal 
    returns (address _liq, uint amount)
  {
    ICommon.Liquidation memory liq = enquireLiquidation(self.pools, poolId);
    address alc_who = _account(who);
    _liq = liq.who;

    ICommon.Info memory info_liq = IAccount(_liq).getSubscriptionInfo(poolId);
    if(_liq == address(0)) revert("11");
    address token = _fetchPoolData(self.pools, poolId).addrs.asset;
    _screenBalance(token, alc_who, liq.debt);
    amount = poolId;
    info_liq.owings = 0;
    require(IAccount(_liq).clearSubscription(poolId), "20");
    amount.safeInitializeInfo(
        info_liq,
        true, // Locks the value in user's wallet until we need to move to the next person
        true, 
        IAccount(alc_who),
        liq.debt,
        token,
        address(0)
    );

    self.pools[poolId].addrs.lastPaid = alc_who;
    amount = info_liq.owings;
    self.pcd.token.moveCollateral(alc_who, _liq, info_liq.colBals);
  }

  /**
    @dev Cancels recent unfil band.
      Only admin of a band can cancel only if no one has join the band.
  */
  function cancelBand(
    Data storage self,
    uint poolId,
    function (address) internal view returns(address) _account
  ) 
    internal
    returns (uint value, address alc)
  {
    ICommon.Pool memory _p = _fetchPoolData(self.pools, poolId);
    alc = _account(_msgSender());
    // uint8 quorum = uint8(pools[poolId].uints.quorum);
    if(_p.uints.mode == ICommon.Mode.NONSTRICT) {
      bool(_p.mems.length == 1).assertUnchained("15");
      delete self.amountExist[_p.uint256s.unit];
    }
    if(_p.uints.mode == ICommon.Mode.STRICT) bool(_p.uint256s.currentPool == _p.uint256s.unit).assertUnchained("15");
    _p.addrs.asset.safeUpdateBalancesInUse(
      IAccount(alc),
      _p.uint256s.unit, 
      true
    );
    value = _p.uint256s.unit;

  }

  /**@dev Payback borrowed fund.
      Note: We are only concerned about who is to pay back and not 
              who is paying back. Ordianrily, any of the members can pay 
              on behalf of another.
  */
  function payback(
    Data storage self,
    uint poolId,
    function (address) internal view returns(address) _account,
    function (uint, ICommon.FuncTag) internal _lock,
    function (uint, ICommon.FuncTag) internal _unlock
  ) 
    internal
    returns(ICommon.GCB memory _return, address alc) 
  {
    ICommon.Pool memory _p = _fetchPoolData(self.pools, poolId);
    ICommon.Info memory _info = IAccount(_account(_msgSender())).getSubscriptionInfo(poolId);
    (, uint _engaged, uint _mostRecent) = IAccount(_account(_msgSender())).getSpendableBalance(_p.addrs.asset);
    bool(_info.owings > 0).assertChained2((_mostRecent - _engaged) >= _info.owings, "5"); // <=====

    _return = ICommon.GCB(
      _p.addrs.asset, 
      _account(_msgSender()), 
      address(0), 
      _info.colBals,
      0,
      ICommon.CR(0, 0),
      ICommon.CR2(_info.payDate, _p.uint256s.unit)
    );

    _info.turnTime = 0;
    _info.owings = 0;
    _info.payDate = 0;
    if(_p.allGh == _p.uints.quorum) {
      _lock(poolId, ICommon.FuncTag.PAYBACK);
      _unlock(poolId, ICommon.FuncTag.COMPLETE);
    }
    uint _poolId = poolId;
    alc = _account(_msgSender());
    _poolId.safeInitializeInfo(
      _info,
      true, // Locks the value in user's wallet until we need to move to the next person
      true, 
      IAccount(_account(_msgSender())),
      _info.owings,
      _p.addrs.asset,
      address(0)
    );

  }

  /**
    Completes the current round.
    - Only member of the band can call.
  */
  function roundUp(
    Data storage self,
    uint poolId
    // address who
  ) 
    internal
    returns(ICommon.Pool memory pool)
  {
    pool = _fetchPoolData(self.pools, poolId);
    bool(pool.allGh == pool.uints.quorum).assertUnchained("16");
    address lastPaid = pool.addrs.lastPaid;
    if(pool.uints.mode == ICommon.Mode.NONSTRICT) delete self.amountExist[pool.uint256s.unit];
    uint _poolId = poolId;
    _poolId.safeSplit(
        IAccount(lastPaid),
        pool.addrs.asset,
        pool.mems,
        self.pcd.feeTo,
        pool.uint256s.unit, 
        IAccount(lastPaid).getSubscriptionInfo(_poolId).owings
    );
  }

  function updatePublicData(
    Data storage self,
    address feeTo,
    address token,
    uint8 penFee,
    uint8 makerFee
  ) internal {
    if(feeTo == address(0)) revert ICommon.ZeroAddress(feeTo);
    if(token == address(0)) revert ICommon.ZeroAddress(token);
    bool(penFee < type(uint8).max).assertChained2(makerFee < type(uint8).max, "19");
    self.pcd = IDigesu.PublicData(feeTo, token, penFee, makerFee);
  }

  // function setSupportedTokens(
  //   address newToken,
  //   function (address) internal _setSupportedToken 
  // ) public override onlyOwner {
  //   _setSupportedTokens(newToken);
  // }


}

